      {/* üîπ –ö–Ω–æ–ø–∫–∞ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      <EditItemButton handleEdit={handleEdit} />
    </ItemCard>
  );
};

export default ItemCardComponent;
