            <input name="propertyType" placeholder="–¢–∏–ø –Ω–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç–∏" onChange={handleChange} required />
            <input name="area" type="number" placeholder="–ü–ª–æ—â–∞–¥—å (–º¬≤)" onChange={handleChange} required />
            <input name="rooms" type="number" placeholder="–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–æ–º–Ω–∞—Ç" onChange={handleChange} required />
            <input name="price" type="number" placeholder="–¶–µ–Ω–∞ (‚ÇΩ)" onChange={handleChange} required />
          </>
        )}

        {/* –ü–æ–ª—è –¥–ª—è –∞–≤—Ç–æ */}
        {formData.type === "–ê–≤—Ç–æ" && (
          <>
            <input name="brand" placeholder="–ú–∞—Ä–∫–∞" onChange={handleChange} required />
            <input name="model" placeholder="–ú–æ–¥–µ–ª—å" onChange={handleChange} required />
            <input name="year" type="number" placeholder="–ì–æ–¥ –≤—ã–ø—É—Å–∫–∞" onChange={handleChange} required />
            <input name="mileage" type="number" placeholder="–ü—Ä–æ–±–µ–≥ (–∫–º)" onChange={handleChange} />
          </>
        )}

        {/* –ü–æ–ª—è –¥–ª—è —É—Å–ª—É–≥ */}
        {formData.type === "–£—Å–ª—É–≥–∏" && (
          <>
            <input name="serviceType" placeholder="–¢–∏–ø —É—Å–ª—É–≥–∏" onChange={handleChange} required />
            <input name="experience" type="number" placeholder="–û–ø—ã—Ç (–ª–µ—Ç)" onChange={handleChange} required />
            <input name="cost" type="number" placeholder="–°—Ç–æ–∏–º–æ—Å—Ç—å (‚ÇΩ)" onChange={handleChange} required />
            <input name="workSchedule" placeholder="–ì—Ä–∞—Ñ–∏–∫ —Ä–∞–±–æ—Ç—ã" onChange={handleChange} />
          </>
