import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle } from "../styles/itemStyles";
import { Typography } from "@mui/material";
import { RealEstateFields, AutoFields, ServiceFields } from "../components/AdditionalFields";
import { EditItemButton } from "../components/EditButton";

function ItemPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => setItem(data));
  }, [id]);

  if (!item) return <Typography align="center">–ó–∞–≥—Ä—É–∑–∫–∞...</Typography>;

  const handleEdit = () => {
    navigate(`/form`, { state: { itemData: item } }); // üîπ –ü–µ—Ä–µ–¥–∞—ë–º –¥–∞–Ω–Ω—ã–µ –≤ /form
  };

  return (
    <ItemContainer>
      <ItemCard>
        <ItemTitle>{item.name}</ItemTitle>
        <Typography>{item.description}</Typography>
        <Typography color="text.secondary">–ö–∞—Ç–µ–≥–æ—Ä–∏—è: {item.type}</Typography>

        {item.type === "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" && <RealEstateFields formData={item} handleChange={() => {}} />}
        {item.type === "–ê–≤—Ç–æ" && <AutoFields formData={item} handleChange={() => {}} />}
        {item.type === "–£—Å–ª—É–≥–∏" && <ServiceFields formData={item} handleChange={() => {}} />}

        {/* üîπ –†–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ /form —Å –¥–∞–Ω–Ω—ã–º–∏ */}
        <EditItemButton handleEdit={handleEdit} />
      </ItemCard>
    </ItemContainer>
  );
}

export default ItemPage;
