import React, { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { uploadImage } from "../../utils/uploadService"; // Функция загрузки изображения
import { FormContainer, FormCard } from "../styles/formStyles";
import { FormFields } from "../components/FormFields";
import { FormActions } from "../components/FormActions";
import FileUpload from "../components/FileUpload";

const FormPage = () => {
  const [searchParams] = useSearchParams();
  const editId = searchParams.get("edit"); // Получаем ID для редактирования
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    image: "",
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  // Загружаем существующие данные при редактировании
  useEffect(() => {
    if (editId) {
      fetch(`/items/${editId}`)
        .then((res) => res.json())
        .then((data) => setFormData(data));
    }
  }, [editId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileSelect = async (file) => {
    const uploadedUrl = await uploadImage(file);
    setFormData({ ...formData, image: uploadedUrl });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const requestData = {
      ...formData,
      area: formData.area ? Number(formData.area) : undefined,
      rooms: formData.rooms ? Number(formData.rooms) : undefined,
      price: formData.price ? Number(formData.price) : undefined,
      year: formData.year ? Number(formData.year) : undefined,
      mileage: formData.mileage ? Number(formData.mileage) : undefined,
      experience: formData.experience ? Number(formData.experience) : undefined,
      cost: formData.cost ? Number(formData.cost) : undefined,
    };

    fetch(editId ? `/items/${editId}` : "/items", {
      method: editId ? "PUT" : "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestData),
    })
      .then(() => navigate("/list"))
      .catch((error) => console.error("Ошибка запроса:", error));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormFields formData={formData} handleChange={handleChange} />
        <FileUpload onFileSelect={handleFileSelect} />
        <FormActions handleSubmit={handleSubmit} editMode={Boolean(editId)} />
      </FormCard>
    </FormContainer>
  );
};

export default FormPage;
