  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name.trim() || !formData.description.trim() || !formData.location.trim() || !formData.type) {
      console.error("Заполните все обязательные поля!");
      return;
    }

    const requestData = {
      name: formData.name.trim(),
      description: formData.description.trim(),
      location: formData.location.trim(),
      type: formData.type,
    };

    if (formData.type === "Недвижимость") {
      requestData.propertyType = formData.propertyType || "Квартира";
      requestData.area = formData.area ? Number(formData.area) : 0;
      requestData.rooms = formData.rooms ? Number(formData.rooms) : 1;
      requestData.price = formData.price ? Number(formData.price) : 0;
    }

    fetch("/items", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestData),
    })
      .then((res) => {
        if (!res.ok) {
          return res.json().then((err) => {
            throw new Error(`Ошибка ${res.status}: ${err.error || "Неизвестная ошибка"}`);
          });
        }
        return res.json();
      })
      .then(() => navigate("/"))
      .catch((error) => console.error("Ошибка запроса:", error));
  };

  return (
    <div>
      <h1>Разместить объявление</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Название" onChange={handleChange} required />
        <textarea name="description" placeholder="Описание" onChange={handleChange} required />
        <input name="location" placeholder="Локация" onChange={handleChange} required />

        <select name="type" onChange={handleChange} required>
          <option value="">Выберите категорию</option>
          <option value="Недвижимость">Недвижимость</option>
          <option value="Авто">Авто</option>
          <option value="Услуги">Услуги</option>
        </select>

        {/* Поля для недвижимости */}
        {formData.type === "Недвижимость" && (
          <>
            <input name="propertyType" placeholder="Тип недвижимости" onChange={handleChange} required />
            <input name="area" type="number" placeholder="Площадь (м²)" onChange={handleChange} required />
            <input name="rooms" type="number" placeholder="Количество комнат" onChange={handleChange} required />
            <input name="price" type="number" placeholder="Цена (₽)" onChange={handleChange} required />
          </>
        )}

        {/* Поля для авто */}
        {formData.type === "Авто" && (
          <>
            <input name="brand" placeholder="Марка" onChange={handleChange} required />
            <input name="model" placeholder="Модель" onChange={handleChange} required />
            <input name="year" type="number" placeholder="Год выпуска" onChange={handleChange} required />
            <input name="mileage" type="number" placeholder="Пробег (км)" onChange={handleChange} />
          </>
        )}

        {/* Поля для услуг */}
        {formData.type === "Услуги" && (
          <>
            <input name="serviceType" placeholder="Тип услуги" onChange={handleChange} required />
            <input name="experience" type="number" placeholder="Опыт (лет)" onChange={handleChange} required />
            <input name="cost" type="number" placeholder="Стоимость (₽)" onChange={handleChange} required />
            <input name="workSchedule" placeholder="График работы" onChange={handleChange} />
          </>
        )}

        <button type="submit">Добавить</button>
      </form>
    </div>
  );
}

export default FormPage;
