import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle, EditButton } from "../styles/itemStyles";
import { Typography, TextField, Button, Select, MenuItem } from "@mui/material";

function ItemPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setItem(data);
        setFormData(data);
      });
  }, [id]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData(item); // Отменяем изменения
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    fetch(`/items/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((updatedItem) => {
        setItem(updatedItem);
        setIsEditing(false);
      });
  };

  if (!item) return <Typography align="center">Загрузка...</Typography>;

  return (
    <ItemContainer>
      <ItemCard>
        {isEditing ? (
          <>
            <TextField fullWidth label="Название" name="name" value={formData.name} onChange={handleChange} required />
            <TextField
              fullWidth
              label="Описание"
              name="description"
              value={formData.description}
              onChange={handleChange}
              multiline
              rows={3}
              required
            />
            <TextField fullWidth label="Локация" name="location" value={formData.location} onChange={handleChange} required />

            <Select fullWidth name="type" value={formData.type} onChange={handleChange} required>
              <MenuItem value="Недвижимость">Недвижимость</MenuItem>
              <MenuItem value="Авто">Авто</MenuItem>
              <MenuItem value="Услуги">Услуги</MenuItem>
            </Select>

            {formData.type === "Недвижимость" && (
              <>
                <TextField fullWidth label="Тип недвижимости" name="propertyType" value={formData.propertyType} onChange={handleChange} />
                <TextField fullWidth label="Площадь (м²)" name="area" type="number" value={formData.area} onChange={handleChange} />
                <TextField fullWidth label="Количество комнат" name="rooms" type="number" value={formData.rooms} onChange={handleChange} />
                <TextField fullWidth label="Цена (₽)" name="price" type="number" value={formData.price} onChange={handleChange} />
              </>
            )}

            {formData.type === "Авто" && (
              <>
                <TextField fullWidth label="Марка" name="brand" value={formData.brand} onChange={handleChange} />
                <TextField fullWidth label="Модель" name="model" value={formData.model} onChange={handleChange} />
                <TextField fullWidth label="Год выпуска" name="year" type="number" value={formData.year} onChange={handleChange} />
                <TextField fullWidth label="Пробег (км)" name="mileage" type="number" value={formData.mileage} onChange={handleChange} />
              </>
            )}

            {formData.type === "Услуги" && (
              <>
                <TextField fullWidth label="Тип услуги" name="serviceType" value={formData.serviceType} onChange={handleChange} />
                <TextField fullWidth label="Опыт (лет)" name="experience" type="number" value={formData.experience} onChange={handleChange} />
                <TextField fullWidth label="Стоимость (₽)" name="cost" type="number" value={formData.cost} onChange={handleChange} />
                <TextField fullWidth label="График работы" name="workSchedule" value={formData.workSchedule} onChange={handleChange} />
              </>
            )}

            <Button variant="contained" color="primary" onClick={handleSave}>
              Сохранить
            </Button>
            <Button variant="outlined" color="secondary" onClick={handleCancel} sx={{ ml: 1 }}>
              Отмена
            </Button>
          </>
        ) : (
          <>
            <ItemTitle>{item.name}</ItemTitle>
            <Typography>{item.description}</Typography>
            <Typography color="text.secondary">Категория: {item.type}</Typography>

            {item.type === "Недвижимость" && (
              <>
                <Typography>Тип: {item.propertyType}</Typography>
                <Typography>Площадь: {item.area} м²</Typography>
                <Typography>Комнат: {item.rooms}</Typography>
                <Typography>Цена: {item.price} ₽</Typography>
              </>
            )}

            {item.type === "Авто" && (
              <>
                <Typography>Марка: {item.brand}</Typography>
                <Typography>Модель: {item.model}</Typography>
                <Typography>Год: {item.year}</Typography>
                <Typography>Пробег: {item.mileage} км</Typography>
              </>
            )}

            {item.type === "Услуги" && (
              <>
                <Typography>Тип услуги: {item.serviceType}</Typography>
                <Typography>Опыт: {item.experience} лет</Typography>
                <Typography>Стоимость: {item.cost} ₽</Typography>
                <Typography>График: {item.workSchedule}</Typography>
              </>
            )}

            <EditButton variant="contained" onClick={handleEdit}>
              Редактировать
            </EditButton>
          </>
        )}
      </ItemCard>
    </ItemContainer>
  );
}

export default ItemPage;
