import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle, EditButton, ItemImage } from "../styles/itemStyles";
import { Typography } from "@mui/material";
import ItemDetails from "../components/ItemDetails"; // ‚úÖ –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º –Ω–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç

const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => setItem(data))
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –æ–±—ä—è–≤–ª–µ–Ω–∏—è:", err));
  }, [id]);

  const handleEdit = () => {
    navigate(`/form?edit=${id}`);
  };

  if (!item) return <Typography align="center">–ó–∞–≥—Ä—É–∑–∫–∞...</Typography>;

  return (
    <ItemContainer>
      <ItemCard>
        {/* –§–æ—Ç–æ –æ–±—ä—è–≤–ª–µ–Ω–∏—è */}
        {item.image ? (
          <ItemImage src={item.image} alt={item.name} />
        ) : (
          <ItemImage src="/placeholder.jpg" alt="–ù–µ—Ç —Ñ–æ—Ç–æ" />
        )}

        <ItemTitle>{item.name}</ItemTitle>
        <Typography>{item.description}</Typography>
        <Typography color="text.secondary">–ö–∞—Ç–µ–≥–æ—Ä–∏—è: {item.type}</Typography>
        <Typography>–õ–æ–∫–∞—Ü–∏—è: {item.location}</Typography>

        {/* üîπ –í—Å—Ç–∞–≤–ª—è–µ–º –Ω–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç */}
        <ItemDetails item={item} />

        <EditButton variant="contained" onClick={handleEdit}>
          –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
        </EditButton>
      </ItemCard>
    </ItemContainer>
  );
};

export default ItemPage;
