import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { FormContainer, FormCard, FormTitle, ImagePreview, UploadButton } from "../styles/formStyles";
import { uploadImage } from "../utils/uploadService";
import { FormFields } from "../components/FormFields";
import FormActions from "../components/FormActions";
import { Button } from "@mui/material";

const FormPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const existingData = location.state?.item || null;

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    image: "", // –ü–æ–ª–µ —Ñ–æ—Ç–æ
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  // ‚úÖ –ü—Ä–∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∑–∞–ø–æ–ª–Ω—è–µ–º —Ñ–æ—Ä–º—É –¥–∞–Ω–Ω—ã–º–∏ –æ–±—ä—è–≤–ª–µ–Ω–∏—è
  useEffect(() => {
    if (existingData) {
      setFormData(existingData);
    }
  }, [existingData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // üîπ –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–æ—Ç–æ
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const imageUrl = await uploadImage(file); // –§—É–Ω–∫—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–æ—Ç–æ
    setFormData({ ...formData, image: imageUrl }); // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤ —Å—Ç–µ–π—Ç
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("–û—Ç–ø—Ä–∞–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ:", formData);

    fetch(existingData ? `/items/${existingData.id}` : "/items", {
      method: existingData ? "PUT" : "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    }).then(() => navigate("/list"));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle>{existingData ? "–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ" : "–†–∞–∑–º–µ—Å—Ç–∏—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ"}</FormTitle>

        <form onSubmit={handleSubmit}>
          {/* ‚úÖ –ü–æ–ª—è —Ñ–æ—Ä–º—ã */}
          <FormFields formData={formData} handleChange={handleChange} />

          {/* üîπ –ü—Ä–µ–≤—å—é —Ñ–æ—Ç–æ —Å –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ–º —Ä–∞–∑–º–µ—Ä–∞ */}
          {formData.image && <ImagePreview src={formData.image} alt="–ü—Ä–µ–≤—å—é" />}

          {/* üîπ –ö–Ω–æ–ø–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–æ—Ç–æ */}
          <UploadButton variant="contained" component="label">
            {formData.image ? "–ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ" : "–î–æ–±–∞–≤–∏—Ç—å —Ñ–æ—Ç–æ"}
            <input type="file" hidden onChange={handleImageUpload} />
          </UploadButton>

          {/* üîπ –ö–Ω–æ–ø–∫–∏ —Ñ–æ—Ä–º—ã */}
          <FormActions isEditing={!!existingData} />
        </form>
      </FormCard>
    </FormContainer>
  );
};

export default FormPage;
