import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle, EditButton } from "../styles/itemStyles";
import { Typography } from "@mui/material";

const ItemPage = () => {
  const { id } = useParams(); // –ü–æ–ª—É—á–∞–µ–º ID –∏–∑ URL
  const navigate = useNavigate();
  const [item, setItem] = useState(null);

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => setItem(data));
  }, [id]);

  const handleEdit = () => {
    navigate(`/form?edit=${id}`); // ‚úÖ –ü–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Å ID
  };

  if (!item) return <Typography align="center">–ó–∞–≥—Ä—É–∑–∫–∞...</Typography>;

  return (
    <ItemContainer>
      <ItemCard>
        <ItemTitle>{item.name}</ItemTitle>
        <Typography>{item.description}</Typography>
        <Typography color="text.secondary">–ö–∞—Ç–µ–≥–æ—Ä–∏—è: {item.type}</Typography>

        <EditButton variant="contained" onClick={handleEdit}>
          –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
        </EditButton>
      </ItemCard>
    </ItemContainer>
  );
};

export default ItemPage;
