import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { FormContainer, FormCard, FormTitle, FormButton } from "../styles/formStyles";
import { BaseFields } from "../components/BaseFields";
import { RealEstateFields, AutoFields, ServiceFields } from "../components/AdditionalFields";

function FormPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const existingData = location.state?.itemData || null; // 🔹 Получаем переданные данные (если есть)

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  // 🔹 При загрузке страницы проверяем, есть ли данные для редактирования
  useEffect(() => {
    if (existingData) {
      setFormData(existingData);
    }
  }, [existingData]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    const method = existingData ? "PUT" : "POST";
    const url = existingData ? `/items/${existingData.id}` : "/items";

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    }).then(() => navigate("/list"));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle variant="h5">{existingData ? "Редактировать объявление" : "Разместить объявление"}</FormTitle>
        <form onSubmit={handleSubmit}>
          <BaseFields formData={formData} handleChange={handleChange} />

          {formData.type === "Недвижимость" && <RealEstateFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Авто" && <AutoFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Услуги" && <ServiceFields formData={formData} handleChange={handleChange} />}

          <FormButton type="submit" variant="contained">
            {existingData ? "Сохранить изменения" : "Добавить"}
          </FormButton>
        </form>
      </FormCard>
    </FormContainer>
  );
}

export default FormPage;
