import React, { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { uploadImage } from "../utils/uploadService";
import { FormContainer, FormCard } from "../styles/formStyles";
import FormFields from "../components/FormFields";
import FormActions from "../components/FormActions";
import FileUpload from "../components/FileUpload";
import ImagePreview from "../components/ImagePreview";
import FormTitle from "../components/FormTitle";

const FormPage = () => {
  const [searchParams] = useSearchParams();
  const editId = searchParams.get("edit"); // Получаем ID редактируемого объявления
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    image: "",
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  const [previewImage, setPreviewImage] = useState(""); // Для отображения превью картинки

  // ✅ Загружаем данные объявления при редактировании
  useEffect(() => {
    if (editId) {
      fetch(`/items/${editId}`)
        .then((res) => {
          if (!res.ok) throw new Error("Ошибка загрузки данных");
          return res.json();
        })
        .then((data) => {
          setFormData(data); // Заполняем форму данными из API
          setPreviewImage(data.image || ""); // Устанавливаем превью изображения
        })
        .catch((error) => console.error("Ошибка загрузки объявления:", error));
    }
  }, [editId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ При выборе файла показываем пользователю картинку сразу
  const handleFileSelect = async (file) => {
    const uploadedUrl = await uploadImage(file);
    if (uploadedUrl) {
      setFormData({ ...formData, image: uploadedUrl });
      setPreviewImage(URL.createObjectURL(file)); // Превью картинки
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const requestData = {
      ...formData,
      area: formData.area ? Number(formData.area) : undefined,
      rooms: formData.rooms ? Number(formData.rooms) : undefined,
      price: formData.price ? Number(formData.price) : undefined,
      year: formData.year ? Number(formData.year) : undefined,
      mileage: formData.mileage ? Number(formData.mileage) : undefined,
      experience: formData.experience ? Number(formData.experience) : undefined,
      cost: formData.cost ? Number(formData.cost) : undefined,
    };

    fetch(editId ? `/items/${editId}` : "/items", {
      method: editId ? "PUT" : "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestData),
    })
      .then(() => navigate("/list"))
      .catch((error) => console.error("Ошибка запроса:", error));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle isEdit={Boolean(editId)} />
        <FormFields formData={formData} handleChange={handleChange} />
        <FileUpload onFileSelect={handleFileSelect} />
        <ImagePreview image={previewImage} />
        <FormActions handleSubmit={handleSubmit} editMode={Boolean(editId)} />
      </FormCard>
    </FormContainer>
  );
};

export default FormPage;
