// import { styled } from "@mui/system";
import { styled } from "@mui/material/styles";

import { Container, Card, CardContent, Button, Typography } from "@mui/material";

// Контейнер страницы
export const HomeContainer = styled(Container)({
  maxWidth: "800px",
  marginTop: "40px",
});

// Заголовок
export const HomeTitle = styled(Typography)({
  textAlign: "center",
  marginBottom: "20px",
});

// Кнопка "Разместить объявление"
export const PostButton = styled(Button)({
  display: "block",
  marginBottom: "20px",
});

// // Карточка объявления
// export const AdCard = styled(Card)({
//   display: "flex",
//   justifyContent: "space-between",
//   alignItems: "center",
//   padding: "16px",
//   marginBottom: "10px",
// });
export const AdCard = styled(Card)({
  display: "flex",  // Делаем карточку флекс-контейнером
  flexDirection: "row", // Оставляем горизонтальное расположение
  alignItems: "center", // Выравниваем элементы по вертикали
  padding: "16px",
  marginBottom: "10px",
  width: "100%", // Карточка занимает всю доступную ширину
});

export const AdImage = styled("img")({
  width: "30%", // Фото занимает 30% ширины карточки
  height: "auto", // Сохраняем пропорции изображения
  aspectRatio: "1 / 1", // Делаем фото квадратным
  objectFit: "cover", // Кадрируем изображение, чтобы оно заполняло квадрат
  borderRadius: "5px", // Закругляем углы (по желанию)
});

export const AdContent = styled(CardContent)({
  width: "70%", // Оставшиеся 70% отводим под текст и кнопку
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  paddingLeft: "16px", // Отступ между фото и текстом
});
