import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle, StyledButton } from "../styles/itemStyles";
import { Typography } from "@mui/material";
import { BaseFields } from "../components/BaseFields";
import { RealEstateFields, AutoFields, ServiceFields } from "../components/AdditionalFields";
import { ActionButtons } from "../components/ActionButtons";
import { EditItemButton } from "../components/EditButton";

function ItemPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setItem(data);
        setFormData(data);
      });
  }, [id]);

  const handleEdit = () => setIsEditing(true);
  const handleCancel = () => {
    setIsEditing(false);
    setFormData(item);
  };

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSave = () => {
    fetch(`/items/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((updatedItem) => {
        setItem(updatedItem);
        setIsEditing(false);
      });
  };

  if (!item) return <Typography align="center">Загрузка...</Typography>;

  return (
    <ItemContainer>
      <ItemCard>
        {isEditing ? (
          <>
            {/* 🔹 Общие поля */}
            <BaseFields formData={formData} handleChange={handleChange} />

            {/* 🔹 Специфичные поля для разных категорий */}
            {formData.type === "Недвижимость" && <RealEstateFields formData={formData} handleChange={handleChange} />}
            {formData.type === "Авто" && <AutoFields formData={formData} handleChange={handleChange} />}
            {formData.type === "Услуги" && <ServiceFields formData={formData} handleChange={handleChange} />}

            {/* 🔹 Кнопки "Сохранить" и "Отмена" */}
            <ActionButtons handleSave={handleSave} handleCancel={handleCancel} />
          </>
        ) : (
          <>
            <ItemTitle>{item.name}</ItemTitle>
            <Typography>{item.description}</Typography>
            <Typography color="text.secondary">Категория: {item.type}</Typography>

            {item.type === "Недвижимость" && <RealEstateFields formData={item} handleChange={() => {}} />}
            {item.type === "Авто" && <AutoFields formData={item} handleChange={() => {}} />}
            {item.type === "Услуги" && <ServiceFields formData={item} handleChange={() => {}} />}

            {/* 🔹 Кнопка "Редактировать" */}
            <EditItemButton handleEdit={handleEdit} />
          </>
        )}
      </ItemCard>
    </ItemContainer>
  );
}

export default ItemPage;
