  };

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSave = () => {
    fetch(`/items/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((updatedItem) => {
        setItem(updatedItem);
        setIsEditing(false);
      });
  };

  if (!item) return <Typography align="center">–ó–∞–≥—Ä—É–∑–∫–∞...</Typography>;

  return (
    <ItemContainer>
      <ItemCard>
        {isEditing ? (
          <>
            {/* üîπ –û–±—â–∏–µ –ø–æ–ª—è */}
            <BaseFields formData={formData} handleChange={handleChange} />

            {/* üîπ –°–ø–µ—Ü–∏—Ñ–∏—á–Ω—ã–µ –ø–æ–ª—è –¥–ª—è —Ä–∞–∑–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π */}
            {formData.type === "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" && <RealEstateFields formData={formData} handleChange={handleChange} />}
            {formData.type === "–ê–≤—Ç–æ" && <AutoFields formData={formData} handleChange={handleChange} />}
            {formData.type === "–£—Å–ª—É–≥–∏" && <ServiceFields formData={formData} handleChange={handleChange} />}

            {/* üîπ –ö–Ω–æ–ø–∫–∏ "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å" –∏ "–û—Ç–º–µ–Ω–∞" */}
            <ActionButtons handleSave={handleSave} handleCancel={handleCancel} />
          </>
        ) : (
          <>
            <ItemTitle>{item.name}</ItemTitle>
            <Typography>{item.description}</Typography>
            <Typography color="text.secondary">–ö–∞—Ç–µ–≥–æ—Ä–∏—è: {item.type}</Typography>

            {item.type === "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" && <RealEstateFields formData={item} handleChange={() => {}} />}
            {item.type === "–ê–≤—Ç–æ" && <AutoFields formData={item} handleChange={() => {}} />}
            {item.type === "–£—Å–ª—É–≥–∏" && <ServiceFields formData={item} handleChange={() => {}} />}

            {/* üîπ –ö–Ω–æ–ø–∫–∞ "–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å" */}
            <EditItemButton handleEdit={handleEdit} />
          </>
        )}
      </ItemCard>
    </ItemContainer>
  );
}

export default ItemPage;
