import { styled } from "@mui/material/styles";
import { Container, Card, Button, Select, Typography } from "@mui/material";

// Контейнер страницы
export const HomeContainer = styled(Container)({
  maxWidth: "800px",
  marginTop: "40px",
});

// Заголовок
export const HomeTitle = styled(Typography)({
  textAlign: "center",
  marginBottom: "20px",
});

// Кнопка "Разместить объявление"
export const PostButton = styled(Button)({
  display: "block",
  marginBottom: "20px",
});

// Карточка объявления
export const AdCard = styled(Card)({
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  padding: "16px",
  marginBottom: "10px",
});
