import ItemDetails from "./ItemDetails";
import { EditItemButton } from "./EditButton";

const ItemCardComponent = ({ item, handleEdit }) => {
  return (
    <ItemCard>
      {/* –õ–µ–≤–∞—è —á–∞—Å—Ç—å - —Ñ–æ—Ç–æ + –Ω–∞–∑–≤–∞–Ω–∏–µ */}
      <ItemImageContainer>
        <ItemImage src={item.image ? item.image : "/placeholder.jpg"} alt={item.name} />
        <ItemTitle>{item.name}</ItemTitle>
      </ItemImageContainer>

      {/* –ü—Ä–∞–≤–∞—è —á–∞—Å—Ç—å - –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
      <ItemContent>
        <Typography>{item.description}</Typography>
        <Typography color="text.secondary">–ö–∞—Ç–µ–≥–æ—Ä–∏—è: {item.type}</Typography>
        <Typography>–õ–æ–∫–∞—Ü–∏—è: {item.location}</Typography>

        {/* üîπ –°—Ç—Ä–æ–∫–∞ —Å –¥–æ–ø. –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ */}
        <ItemDetailsRow>
          <ItemDetails item={item} />
        </ItemDetailsRow>

        {/* üîπ –ö–Ω–æ–ø–∫–∞ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
        <EditItemButton handleEdit={handleEdit} />
      </ItemContent>
    </ItemCard>
  );
};

export default ItemCardComponent;
