import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { FormContainer, FormCard } from "../styles/formStyles";
import { BaseFields } from "../components/BaseFields";
import { RealEstateFields, AutoFields, ServiceFields } from "../components/AdditionalFields";
import FormHeader from "../components/FormHeader";
import FormSubmitButton from "../components/FormSubmitButton";
import FormImageUploader from "../components/FormImageUploader";

function FormPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const existingData = location.state?.itemData || null;

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    image: "",
  });

  const [imageFile, setImageFile] = useState(null);

  useEffect(() => {
    if (existingData) {
      setFormData(existingData);
    }
  }, [existingData]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let imageUrl = formData.image;

    if (imageFile) {
      const formDataImage = new FormData();
      formDataImage.append("image", imageFile);

      const response = await fetch("/upload", {
        method: "POST",
        body: formDataImage,
      });

      const data = await response.json();
      imageUrl = data.imageUrl;
    }

    const method = existingData ? "PUT" : "POST";
    const url = existingData ? `/items/${existingData.id}` : "/items";

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...formData, image: imageUrl }),
    }).then(() => navigate("/list"));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormHeader isEdit={!!existingData} />
        <form onSubmit={handleSubmit}>
          <BaseFields formData={formData} handleChange={handleChange} />

          <FormImageUploader handleFileChange={handleFileChange} preview={formData.image} />

          {formData.type === "Недвижимость" && <RealEstateFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Авто" && <AutoFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Услуги" && <ServiceFields formData={formData} handleChange={handleChange} />}

          <FormSubmitButton isEdit={!!existingData} />
        </form>
      </FormCard>
    </FormContainer>
  );
}

export default FormPage;
