import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { FormContainer, FormCard, FormTitle, ImagePreview, UploadButton } from "../styles/formStyles";
import { uploadImage } from "../utils/uploadService";
import { FormFields } from "../components/FormFields";
import BackToHomeButton from "../components/BackToHomeButton";
import FormActions from "../components/FormActions"; // ‚úÖ –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º FormActions

const FormPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const existingData = location.state?.item || null;

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    image: "",
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  const [isDirty, setIsDirty] = useState(false); // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ

  useEffect(() => {
    if (existingData) {
      setFormData(existingData);
    }
  }, [existingData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setIsDirty(true); // üîπ –ö–Ω–æ–ø–∫–∞ –∞–∫—Ç–∏–≤–∏—Ä—É–µ—Ç—Å—è –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ —Ñ–æ—Ä–º—ã
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const imageUrl = await uploadImage(file);
    setFormData({ ...formData, image: imageUrl });
    setIsDirty(true); // üîπ –§–æ—Ç–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ ‚Üí –∞–∫—Ç–∏–≤–∏—Ä—É–µ–º –∫–Ω–æ–ø–∫—É
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("–û—Ç–ø—Ä–∞–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ:", formData);

    fetch(existingData ? `/items/${existingData.id}` : "/items", {
      method: existingData ? "PUT" : "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    }).then(() => {
      navigate("/list");
      setIsDirty(false); // üîπ –ü–æ—Å–ª–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–Ω–æ–≤–∞ –æ—Ç–∫–ª—é—á–∞–µ–º –∫–Ω–æ–ø–∫—É
    });
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle>{existingData ? "–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ" : "–†–∞–∑–º–µ—Å—Ç–∏—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ"}</FormTitle>

        <form onSubmit={handleSubmit}>
          <FormFields formData={formData} handleChange={handleChange} />

          {formData.image && <ImagePreview src={formData.image} alt="–ü—Ä–µ–≤—å—é" />}

          <UploadButton variant="contained" component="label">
            {formData.image ? "–ò–∑–º–µ–Ω–∏—Ç—å —Ñ–æ—Ç–æ" : "–î–æ–±–∞–≤–∏—Ç—å —Ñ–æ—Ç–æ"}
            <input type="file" hidden onChange={handleImageUpload} />
          </UploadButton>

          {/* üîπ –ü–µ—Ä–µ–¥–∞–µ–º `isDirty` –≤ FormActions */}
          <FormActions isDirty={isDirty} />
        </form>

        <BackToHomeButton /> 

      </FormCard>
    </FormContainer>
  );
};

export default FormPage;
