import React from "react";
import { Link } from "react-router-dom";
import { AdCard } from "../styles/homeStyles";
import { Button, Typography, CardContent } from "@mui/material";

const AdCardComponent = ({ item }) => {
  return (
    <AdCard>
      <img
        src={item.image ? item.image : "/placeholder.jpg"} // 🔹 Заглушка, если фото нет
        alt="Фото объявления"
        style={{ width: "100%", objectFit: "cover", borderRadius: "5px" }}
      />
      <CardContent>
        <Typography variant="h6">{item.name}</Typography>
        <Typography color="text.secondary">
          {item.location} · {item.type}
        </Typography>
      </CardContent>
      <Button variant="contained" component={Link} to={`/item/${item.id}`}>
        Открыть
      </Button>
    </AdCard>
  );
};

export default AdCardComponent;
