import React from "react";
import { Link } from "react-router-dom";
import { AdCard, AdImage, AdContent } from "../styles/homeStyles";
import { Button, Typography } from "@mui/material";

const AdCardComponent = ({ item }) => {
  return (
    <AdCard>
      <AdImage
        src={item.image ? item.image : "/placeholder.jpg"} // Заглушка, если фото нет
        alt="Фото объявления"
      />
      <AdContent>
        <Typography variant="h6">{item.name}</Typography>
        <Typography color="text.secondary">
          {item.location} · {item.type}
        </Typography>
        <Button variant="contained" component={Link} to={`/item/${item.id}`}>
          Открыть
        </Button>
      </AdContent>
    </AdCard>
  );
};

export default AdCardComponent;
