import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { HomeContainer, HomeTitle, PostButton, AdCard } from "../styles/homeStyles";
import { MenuItem, Select, Button, Typography, CardContent } from "@mui/material";

function Home() {
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState("");

  useEffect(() => {
    fetch("/items")
      .then((res) => res.json())
      .then((data) => setItems(data));
  }, []);

  const filteredItems = filter ? items.filter((item) => item.type === filter) : items;

  return (
    <HomeContainer>
      <HomeTitle variant="h4">–û–±—ä—è–≤–ª–µ–Ω–∏—è</HomeTitle>

      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 20 }}>
        <PostButton variant="contained" color="success" component={Link} to="/form">
          –†–∞–∑–º–µ—Å—Ç–∏—Ç—å –æ–±—ä—è–≤–ª–µ–Ω–∏–µ
        </PostButton>

        <Select fullWidth value={filter} onChange={(e) => setFilter(e.target.value)} displayEmpty>
          <MenuItem value="">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</MenuItem>
          <MenuItem value="–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å">–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å</MenuItem>
          <MenuItem value="–ê–≤—Ç–æ">–ê–≤—Ç–æ</MenuItem>
          <MenuItem value="–£—Å–ª—É–≥–∏">–£—Å–ª—É–≥–∏</MenuItem>
        </Select>
      </div>

      {filteredItems.map((item) => (
        <AdCard key={item.id}>
          <CardContent>
            <Typography variant="h6">{item.name}</Typography>
            <Typography color="text.secondary">{item.type}</Typography>
          </CardContent>
          <Button variant="contained" component={Link} to={`/item/${item.id}`}>
            –û—Ç–∫—Ä—ã—Ç—å
          </Button>
        </AdCard>
      ))}
    </HomeContainer>
  );
}

export default Home;
