import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FormContainer, FormCard, FormTitle, FormInput, FormButton } from "../styles/formStyles";
import { MenuItem, Select, Typography } from "@mui/material";

function FormPage() {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
    propertyType: "",
    area: "",
    rooms: "",
    price: "",
    brand: "",
    model: "",
    year: "",
    mileage: "",
    serviceType: "",
    experience: "",
    cost: "",
    workSchedule: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name.trim() || !formData.description.trim() || !formData.location.trim() || !formData.type) {
      console.error("Заполните все обязательные поля!");
      return;
    }

    const requestData = { ...formData };

    console.log("Отправляем данные:", requestData);

    fetch("/items", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestData),
    }).then(() => navigate("/list"));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle variant="h5">Разместить объявление</FormTitle>
        <form onSubmit={handleSubmit}>
          <FormInput label="Название" name="name" onChange={handleChange} required />
          <FormInput label="Описание" name="description" onChange={handleChange} required multiline rows={3} />
          <FormInput label="Локация" name="location" onChange={handleChange} required />

          {/* Выбор категории */}
          <Select fullWidth name="type" value={formData.type} onChange={handleChange} required>
            <MenuItem value="">Выберите категорию</MenuItem>
            <MenuItem value="Недвижимость">Недвижимость</MenuItem>
            <MenuItem value="Авто">Авто</MenuItem>
            <MenuItem value="Услуги">Услуги</MenuItem>
          </Select>

          {/* Поля для недвижимости */}
          {formData.type === "Недвижимость" && (
            <>
              <FormInput label="Тип недвижимости" name="propertyType" onChange={handleChange} required />
              <FormInput label="Площадь (м²)" name="area" type="number" onChange={handleChange} required />
              <FormInput label="Количество комнат" name="rooms" type="number" onChange={handleChange} required />
              <FormInput label="Цена (₽)" name="price" type="number" onChange={handleChange} required />
            </>
          )}

          {/* Поля для авто */}
          {formData.type === "Авто" && (
            <>
              <FormInput label="Марка" name="brand" onChange={handleChange} required />
              <FormInput label="Модель" name="model" onChange={handleChange} required />
              <FormInput label="Год выпуска" name="year" type="number" onChange={handleChange} required />
              <FormInput label="Пробег (км)" name="mileage" type="number" onChange={handleChange} />
            </>
          )}

          {/* Поля для услуг */}
          {formData.type === "Услуги" && (
            <>
              <FormInput label="Тип услуги" name="serviceType" onChange={handleChange} required />
              <FormInput label="Опыт (лет)" name="experience" type="number" onChange={handleChange} required />
              <FormInput label="Стоимость (₽)" name="cost" type="number" onChange={handleChange} required />
              <FormInput label="График работы" name="workSchedule" onChange={handleChange} />
            </>
          )}

          <FormButton type="submit" variant="contained">
            Добавить
          </FormButton>
        </form>
      </FormCard>
    </FormContainer>
  );
}

export default FormPage;
