import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ItemContainer, ItemCard, ItemTitle, EditButton, ItemImage } from "../styles/itemStyles";
import { Typography } from "@mui/material";
import ItemDetails from "../components/ItemDetails"; // ✅ Импортируем новый компонент
import { EditItemButton } from "../components/EditButton";
import ItemCardComponent from "../components/ItemCardComponent";

const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);

  useEffect(() => {
    fetch(`/items/${id}`)
      .then((res) => res.json())
      .then((data) => setItem(data))
      .catch((err) => console.error("Ошибка загрузки объявления:", err));
  }, [id]);

  const handleEdit = () => {
    navigate(`/form?edit=${id}`);
  };

  if (!item) return <Typography align="center">Загрузка...</Typography>;

  return <ItemCardComponent item={item} handleEdit={handleEdit} />;
};

export default ItemPage;
