import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FormContainer, FormCard, FormTitle, FormButton } from "../styles/formStyles";
import { BaseFields } from "../components/BaseFields";
import { RealEstateFields, AutoFields, ServiceFields } from "../components/AdditionalFields";

function FormPage() {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    type: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("/items", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    }).then(() => navigate("/list"));
  };

  return (
    <FormContainer>
      <FormCard>
        <FormTitle variant="h5">Разместить объявление</FormTitle>
        <form onSubmit={handleSubmit}>
          {/* 🔹 Общие поля */}
          <BaseFields formData={formData} handleChange={handleChange} />

          {/* 🔹 Специфичные поля для категорий */}
          {formData.type === "Недвижимость" && <RealEstateFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Авто" && <AutoFields formData={formData} handleChange={handleChange} />}
          {formData.type === "Услуги" && <ServiceFields formData={formData} handleChange={handleChange} />}

          <FormButton type="submit" variant="contained">Добавить</FormButton>
        </form>
      </FormCard>
    </FormContainer>
  );
}

export default FormPage;
