import React from "react";
import { StyledTextField, StyledSelect, StyledMenuItem } from "../styles/formStyles";
import { isValidTitle, countWords } from "../utils/validation"; // ‚úÖ –ò–º–ø–æ—Ä—Ç –≤–∞–ª–∏–¥–∞—Ü–∏–∏

export const FormFields = ({ formData, handleChange }) => {
  // üîπ –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –≤–≤–æ–¥–∞ –Ω–∞–∑–≤–∞–Ω–∏—è (—Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π)
  const handleTitleChange = (e) => {
    if (isValidTitle(e.target.value)) {
      handleChange(e);
    }
  };

  return (
    <>
      {/* üîπ –ü–æ–ª–µ –≤–≤–æ–¥–∞ —Å –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ–º 10 —Å–ª–æ–≤ */}
      <StyledTextField
        label="–ù–∞–∑–≤–∞–Ω–∏–µ"
        name="name"
        value={formData.name}
        onChange={handleTitleChange} // ‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ñ—É–Ω–∫—Ü–∏—é –∏–∑ utils
        required
        helperText={`${countWords(formData.name)} / 10 —Å–ª–æ–≤`} // ‚úÖ –°—á–∏—Ç–∞–µ–º —Å–ª–æ–≤–∞ —á–µ—Ä–µ–∑ utils
      />

      <StyledTextField label="–û–ø–∏—Å–∞–Ω–∏–µ" name="description" value={formData.description} onChange={handleChange} multiline rows={3} required />
      <StyledTextField label="–õ–æ–∫–∞—Ü–∏—è" name="location" value={formData.location} onChange={handleChange} required />

      <StyledSelect name="type" value={formData.type} onChange={handleChange} displayEmpty required>
        <StyledMenuItem value="" disabled>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</StyledMenuItem>
        <StyledMenuItem value="–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å">–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å</StyledMenuItem>
        <StyledMenuItem value="–ê–≤—Ç–æ">–ê–≤—Ç–æ</StyledMenuItem>
        <StyledMenuItem value="–£—Å–ª—É–≥–∏">–£—Å–ª—É–≥–∏</StyledMenuItem>
      </StyledSelect>

      {formData.type === "–ù–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç—å" && (
        <>
          <StyledTextField label="–¢–∏–ø –Ω–µ–¥–≤–∏–∂–∏–º–æ—Å—Ç–∏" name="propertyType" value={formData.propertyType} onChange={handleChange} required />
          <StyledTextField label="–ü–ª–æ—â–∞–¥—å (–º¬≤)" name="area" type="number" value={formData.area} onChange={handleChange} required />
          <StyledTextField label="–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–æ–º–Ω–∞—Ç" name="rooms" type="number" value={formData.rooms} onChange={handleChange} required />
          <StyledTextField label="–¶–µ–Ω–∞ (‚ÇΩ)" name="price" type="number" value={formData.price} onChange={handleChange} required />
        </>
      )}

      {formData.type === "–ê–≤—Ç–æ" && (
        <>
          <StyledTextField label="–ú–∞—Ä–∫–∞" name="brand" value={formData.brand} onChange={handleChange} />
          <StyledTextField label="–ú–æ–¥–µ–ª—å" name="model" value={formData.model} onChange={handleChange} />
          <StyledTextField label="–ì–æ–¥ –≤—ã–ø—É—Å–∫–∞" name="year" type="number" value={formData.year} onChange={handleChange} />
